	/free/bin/ncdump -x "$tmpdir/in.nc" > "$tmpdir/in2.xml"	
	sed -e 's/&#0;//g' "$tmpdir/in2.xml" > "$tmpdir/in3.xml"
	sed -e 2d "$tmpdir/in3.xml" > "$tmpdir/in1.xml"
	sed -e  '2 i <netcdf>' "$tmpdir/in1.xml" > "$tmpdir/in11.xml"
	sed -e '3 i <path>'$uri_path'/</path>'  "$tmpdir/in11.xml" > "$tmpdir/in22.xml"
	sed -e '4 i <title>'$basename'</title>'  "$tmpdir/in22.xml" > "$tmpdir/in3.xml"
	sed -e '4 i <gcmd>'$type'</gcmd>'  "$tmpdir/in3.xml" > "$tmpdir/in1.xml"
	sed -e '4 i <filesize>'$filesizem'</filesize>'  "$tmpdir/in1.xml" > "$tmpdir/in3.xml"
	sed -e '4 i <ext>'$ext'</ext>'  "$tmpdir/in3.xml" > "$tmpdir/in1.xml"
	sed -e '4 i <bg>'$browse'</bg>'  "$tmpdir/in1.xml" > "$tmpdir/in3.xml"
	xsltproc $xslfile "$tmpdir/in3.xml" > "$tmpdir/in44.xml" 
	xsltproc --stringparam collFile $isocofile granule.xsl "$tmpdir/in44.xml" > '/nodc/web/data.nodc/htdocs/nodc/archive/metadata/test/granule/iso/ghrsst_new/'$folder'/'$subdir'/'$basename'.xml'
	xsltproc --stringparam collFile $isocofile granule.xsl "$tmpdir/in44.xml" > '/nodc/web/data.nodc/htdocs/nodc/archive/metadata/test/granule/iso/granule_new/ghrsst/'$folder'/'$basename'.xml'		
	else 
		echo "No data was found!"
	fi
	
	rm -r "$tmpdir"
	exitcode=$?
	exit $exitcode
done

